import React from 'react';
import arrowDown from './assets/arrowDown.svg';
import { jsPDF } from 'jspdf';
import { useGlobalContext } from './Context';

const DownloadPdf = () => {
  const { outputText } = useGlobalContext();
  const { value, fontSize } = outputText;

  const generatePDF = () => {
    if (!value) {
      alert('Text is empty!');
      return;
    }

    const doc = new jsPDF();

    // Add the base64 font or external font
    const base64Font =
      '';
    doc.addFileToVFS('Font1LigTTF.ttf', base64Font); // Replace with your base64 font string
    doc.addFont('Font1LigTTF.ttf', 'Font1LigTTF', 'normal'); // Register the font

    // Set font and apply ligature option
    doc.setFont('Font1LigTTF', 'normal');

    // Convert text to uppercase
    const upperCaseText = value.toUpperCase();

    // Render the ligature text
    doc.setFontSize(fontSize);
    doc.text(upperCaseText, 10, 10, { maxWidth: 180 });

    // Save the PDF
    doc.save('TextInBosancica.pdf');
  };

  return (
    <button
      type='button'
      className='rounded-custom-radius font-semibold w-full h-full gap-5 flex flex-col items-center justify-center'
      onClick={generatePDF}
    >
      <img
        src={arrowDown}
        alt='Download PDF'
        className=' md-black filter-white size-[33px] md:size-[49px]'
      />
      <h4 className='hidden md:block text-[30px] leading-[1.5] w-[6rem]'>
        Export PDF
      </h4>
    </button>
  );
};

export default DownloadPdf;
